mapper.update:
update mapping set pj_bookmark =
0 1
where user_id = #{user_id} and pj_num = #{pj_num};

update user_project
set pj_startdate = #{pjStartdate}, pj_enddate = #{pjEnddate}, pj_name = #{pjName}, pj_description = #{pjDescription}
where pj_num = #{pjNum}

update mapping set is_observer =
0 1
where pj_num = #{pjNum} and user_id = #{userId}

mapper.delete:
delete
from mapping
where user_id = #{userId} and pj_num = #{pjNum}
